import React from "react"

import propTypes from "prop-types"

import * as style from "./Button.module.scss"

export const Button = (
  {
    icon,
    iconClass,
    spinner,
    text,
    textClass,
    wrapperClass
  }) => {
  return (
    <div className={`${wrapperClass} ${style.container}`}>
      {icon && <span>{icon}</span>}
      <span className={textClass}>
        <p>{text}</p>
      </span>
    </div>
  )
}

Button.defaultProps = {
  icon: true,
  iconClass: "",
  spinner: false,
  text: "Save",
  textClass: "",
  wrapperClass: ""
}

Button.propTypes = {
  icon: propTypes.node,
  iconClass: propTypes.string,
  text: propTypes.string,
  textClass: propTypes.string,
  wrapperClass: propTypes.string

}
