import React from "react"

import propTypes from "prop-types"

import * as style from "./Button.module.scss"

export const Button = (
  {
    text,
    mainClass,
    iconClass,
    textClass,
    icon,
    renderIcon,
    spinner
  }) => {
  return (
    <div className={`${props.wrapperClass} ${style.container}`}>
      {text}
    </div>
  )
}

Button.defaultProps = {
  text: "Save",
  wrapperClass: ""
}

Button.propTypes = {
  text: propTypes.string,
  wrapperClass: propTypes.string
}
