import React from "react"

import propTypes from "prop-types"

import {TiChevronRightOutline} from "../../assets/icons/icons"
import * as style from "./Button.module.scss"

export const Button = (
  {
    icon,
    iconClass,
    isIconShown,
    isLoading,
    content,
    spinner,
    text,
    textClass,
    wrapperClass,
  }) => {

  const onLoading = () => {
    console.log("vahid")
  }

  return (
    <div className={`${wrapperClass} ${style.container}`} onClick={() => onLoading()}>
      {isIconShown &&
      <span className={`${iconClass} ${style.icon}`}>
        {!isLoading ? icon : "spinner"}
      </span>
      }
      <span className={`${textClass}`}>
        {!isLoading && text}
      </span>
      <div>
        {content}
      </div>
    </div>
  )
}

Button.defaultProps = {
  icon: <TiChevronRightOutline/>,
  iconClass: "",
  isIconShown: true,
  isLoading: false,
  text: "Save",
  textClass: "",
  wrapperClass: "",
  content: ""
}

Button.propTypes = {
  icon: propTypes.element,
  iconClass: propTypes.string,
  isIconShown: propTypes.bool,
  isLoading: propTypes.bool,
  text: propTypes.string,
  textClass: propTypes.string,
  wrapperClass: propTypes.string,
  content: propTypes.string
}
