import React from "react"

class ButtonContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: ""
    }
  }

  increment = () => {
    this.setState({
      count: this.state.count + 1
    })
  }

  decrement = () => {
    this.setState({
      count: this.state.count - 1
    })
  }

  reset = () => {
    this.setState({
      count: 0
    })
  }

  render() {
    return (
      this.increment,
        this.decrement,
        this.reset
    )
  }
}

export default ButtonContainer
